mapper.select:
select id, username, password, email, avatar, remark, authority
from user
where id = #{id}

select id, username, password, email, avatar, remark, authority
from user
where username = #{username}

select id, username, password, email, avatar, remark, authority
from user
where username = #{username}
  and password = #{password}

mapper.update:
update user

    username = #{username}
    password = #{password}
    email = #{email}
    avatar = #{avatar}
    remark = #{remark}
    authority = #{authority}

where id = #{id}
