mapper.select:
select id, account, password, username, tel, remark, is_admin
from publishers
where account = #{account}
  and password = #{password}

select id, account, password, username, tel, remark, is_admin
from publishers

    and account = #{account}
    and username like concat('%',#{username},%)
    and tel = #{tel}

mapper.update:
update publishers

    account = #{account}
    password = #{password}
    username = #{username}
    tel = #{tel}
    remark = #{remark}
    is_admin = #{isAdmin}

where id = #{id}
