mapper.update:
update question
questionnaire_id = #{questionnaireId}, title = #{title}, type = #{type}, is_required = #{isRequired}, is_private = #{isPrivate}
where id = #{id}

mapper.select:
select id, questionnaire_id, order_number, title, type, is_required, is_private
from question
where id = #{id}

select MAX(order_number)
from question
where questionnaire_id = #{questionnaireId}
